mapper.select:
SELECT m.id,m.name,m.parentid,m.iconCls,m.url, (select count(1) from sysmenu s where s.parentId!=0 and s.parentId=m.id) countChildrens FROM sysmenu m LEFT JOIN role_menu rm ON m.id = rm.menu_id LEFT JOIN sysrole r ON rm.role_id = r.id LEFT JOIN user_role ur ON r.id = ur.roleId LEFT JOIN usertable u ON ur.userId = u.UID WHERE m.enable=1
AND u.UID = #{userId}
AND m.id NOT IN( SELECT tt.id FROM (SELECT m.id, (SELECT COUNT(parentId) FROM sysmenu sp WHERE sp.enable=1 AND sp.parentId=m.id AND m.parentId=0 ) countch FROM sysmenu m WHERE m.parentId=0 ) tt where countch =0 ) ORDER BY m.sequence

	SELECT u.UID,u.NAME,u.SEX,u.ACCOUNT,u.PASSWORD,u.HEADURL,u.regtime
    FROM usertable u WHERE 1=1

		AND NAME LIKE concat('%',#{user.NAME},'%')


		ORDER BY ${page.sort} ${page.order}


		limit #{page.start},#{page.end}

SELECT u.UID,u.NAME,u.SEX,u.ACCOUNT,u.PASSWORD,u.HEADURL,u.regtime,r.id AS roleId,r.name AS roleName FROM usertable u left join user_role ul on ul.userId=u.UID left join sysrole r on ul.roleId=r.id WHERE 1=1
AND u.NAME LIKE concat('%',#{user.NAME},'%')
ORDER BY ${page.sort} ${page.order}
limit #{page.start},#{page.end}

mapper.update:
UPDATE usertable
NAME=#{NAME}, SEX=#{SEX}, ACCOUNT=#{ACCOUNT}, PASSWORD=#{PASSWORD}, HEADURL=#{HEADURL}, DESCRIPTION=#{DESCRIPTION}, regtime=#{regtime}
WHERE UID=#{UID}
